mapper.select:
select count(*) from taste_board

	
		
			location=#{location}
		
		
			writer=#{writer}
		
		
			article_no=-1

select * from 
(select rownum,t.* from taste_board t order by article_no desc) 
where rownum between #{beginRow} and #{endRow}

select * from 
(select rownum,t.* from taste_board t order by hits desc, article_no desc) 
where rownum between #{beginRow} and #{endRow}

select * from 
(select rownum,t.good-t.bad as rank,t.* from taste_board t order by rank desc, article_no desc) 
where rownum between #{beginRow} and #{endRow}

select * from 
(select rownum,t.* from taste_board t where location=#{location} order by article_no desc) 
where rownum between #{page.beginRow} and #{page.endRow}

select * from 
(select rownum,t.* from taste_board t where writer=#{writer} order by article_no desc) 
where rownum between #{page.beginRow} and #{page.endRow}
