mapper.select:

	select * from (
	select rownum as rnum, m.* 
	from member m order by rnum desc)
	where rnum between #{beginRow} and #{endRow}


	select * from (
	select rownum as rnum, m.* 
	from member m where exp<10000000 order by exp desc)
	where rnum between #{beginRow} and #{endRow}

mapper.update:
update member set password=#{password}, name=#{name}, city=#{city}, sigungu=#{sigungu}, eupmyeondong=#{eupmyeondong}, address=#{address}, gender=#{gender},
birth=#{birth}, tel=#{tel}, exp=#{exp}, point=#{point}, profile_img=#{profileImg}
where id=#{id}
